function opencloseNav(){window.innerWidth>700?openNav():closeNav()}function openNav(){document.querySelector(".Nav").style.width="250px"}function closeNav(){document.querySelector(".Nav").style.width="0px"}function openPage(e){document.querySelectorAll(".page"),window.innerWidth<700&&closeNav(),loadHTMLPage("pages/"+e+".html",document.getElementById("page"))}function getPageString(e){return decodeURI((new RegExp("[?]"+e+"=([^&]+?)($)").exec(location.search)||[null,""])[1].replace(/\+/g,"%20"))||null}function loadHTMLPage(e,n){req=new XMLHttpRequest,req.open("GET",e,!1),req.send(null),n.innerHTML=req.responseText}document.addEventListener("DOMContentLoaded",function(){var e=getPageString("navigate");openPage(null!==e?e:"Home"),opencloseNav()}),window.addEventListener("resize",function(){opencloseNav()});